CREATE FUNCTION IsPrimaryKey ( @TblName nvarchar(100),	@ColName nvarchar(100)) RETURNS int AS BEGIN DECLARE @IsPrimary int SELECT @IsPrimary = count(*)	from 	INFORMATION_SCHEMA.TABLE_CONSTRAINTS pk ,		INFORMATION_SCHEMA.KEY_COLUMN_USAGE c	where 	pk.TABLE_NAME = @TblName	and COLUMN_NAME = @ColName	and	CONSTRAINT_TYPE = 'PRIMARY KEY'	and	c.TABLE_NAME = pk.TABLE_NAME	and	c.CONSTRAINT_NAME = pk.CONSTRAINT_NAME		RETURN @IsPrimary END